bool checkName(char* filenmae) {
	strcpy(exeRname, PER_PNAME);
	strcat(exeRname, " Vers. ");
	strcat(exeRname, PER_VERS);
	strcat(exeRname, ".exe");
	char xda[2] = "";
	xda[1] = '\0';
	char* pxd;
	int cnt = 0;
	pxd = filenmae;
	do {
		xda[0] = pxd[0];
		//printf("%c", pxd[0]);
		pxd++;
		cnt++;
	} while (strcmp(pxd, exeRname) != 0 && cnt < 10001);
	if (cnt >= 10001) {
		color(ROTh);
		printf("Program has the wrong name!!\nIt should be named \"%s\"!!!\n", exeRname);
		rcolor;
		return 0;
	}
	return 1;
}

