void wait(int tim) {
	clock_t begin;
	int ti = 0;
	begin = clock();
	while ((((clock() - begin) / (CLOCKS_PER_SEC / 1000))) <= tim);
}

bool waitS(int tim, bool start) {
	static clock_t begin;
	if (start)
		begin = clock();

	int ti = 0;
	return	!((((clock() - begin) / (CLOCKS_PER_SEC / 1000))) <= tim);
}
long int sonlen(struct DbLsNote* pNotes) {
	unsigned int i = 0;
	long int x = 0;
	while (pNotes[i].pitch != DB_LS_LAST) {
		x += notLenC[pNotes[i].length];
		i++;
	}
	return x;
}
long int sonlenDB(struct DbLsNote* pNotes) {
	unsigned int i = 0;
	long int x = 0;
	while (pNotes[i].pitch != DB_LS_LAST) {
		x += notLenDB[pNotes[i].length] * 50;
		i++;
	}
	return x;
}
int realLen(uint8_t len) {
	return notLenC[len];
}
void playSong(struct DbLsNote* pNotes) {
	long int lenmax = 0;
	long int lencurr = 0;
	lenmax = sonlen(pNotes);
	printf("The song lasts %0.3lf seconds!\n", (double)lenmax / 1000);
	color(GELBh);
	printf("Press enter to stop!\n");
	rcolor;
	unsigned int i = 0;
	bool end = 0;
	int cwait = 0;
	lenmax = sonlen(pNotes);
	color(VIOLETTh);
	while (pNotes[i].pitch != DB_LS_LAST) {
		printf("Status %0.2lf%% played (remaining time %0.3lf sec)                \r", percent((lenmax), lencurr), (double)(lenmax - lencurr) / 1000);
		if (pNotes[i].pitch != DB_LS_PAUSE) {
			//printf("BEEP: %i\n", pNotes[i].pitch);
			Beep((DWORD)pNotes[i].pitch/* + (DB_LS_C2 - DB_LS_C3)*/, (DWORD)realLen(pNotes[i].length) / 0.7);
			cwait = (realLen(pNotes[i].length) * 2);
			//cwait = 0;
		}
		else {
			//printf("WAIT : %i\n", pNotes[i].length);
			cwait = (realLen(pNotes[i].length));
		}
		waitS(cwait, 1);
		while (!waitS(cwait, 0) && !(end = anyKey()));
		if (end) {
			flush();
			break;
		}
		lencurr += realLen(pNotes[i].length);
		//wait(75);
		i++;
	}
	for (int io = 0; io != 100; io++)
		printf(" ");
	printf("\r");
	if (!end) {
		color(GRUENh);
		printf("Status %0.2lf%% played\n", percent(1, 1));
	}
	else {
		printf("Status %0.2lf%% played - exit\n", percent((lenmax), lencurr));
	}
	rcolor;
}
